vec4 Effect2(vec2 uv) {
    vec3 color = hsl(0.5 + 0.1 * sin(iTime * 0.5), 0.8, 0.6);

    float time = iTime * 0.5;
    vec2 st = uv * 4.0; // Scale up for more detail

    // Create a swirling pattern using polar coordinates
    vec2 pos = vec2(
        sin(time + st.x) * 0.5 + 0.5,
        cos(time + st.y) * 0.5 + 0.5
    );

    // Add noise and turbulence for dynamic effect
    float noise1 = turbulent(vec3(st, time));
    float noise2 = noise(vec2(pos.x + time, pos.y + time));

    // Combine patterns with color transformations
    vec3 finalColor = hsl2rgb(vec3(
        0.5 * noise1 + 0.5,
        0.8,
        0.6 + 0.4 * noise2
    ));

    // Add light trails effect
    float glow = sin(time + st.x * 0.5) * cos(st.y * 0.5);
    finalColor += vec3(1.0, 0.8, 0.6) * max(glow, 0.0) * 2.0;

    return vec4(finalColor, 1.0);
}
