vec4 Effect2(vec2 uv) {
    // Create a time-varying pattern using sine and cosine functions
    float t = iTime * 0.5;

    // Generate rotating geometric patterns
    vec2 st = uv * 10.0 + vec2(sin(t), cos(t));
    float f = sin(st.x) * cos(st.y) + sin(t * 2.0);

    // Add turbulence for dynamic movement
    f += turbulent(uv * 5.0 + vec2(sin(t), cos(t))) * 0.5;

    // Create shifting color palette using HSL
    vec3 color = hsl2rgb(vec3(
        sin(f * 0.5 + t) * 0.5 + 0.5,   // Hue
        0.7,                              // Saturation
        0.5                               // Lightness
    ));

    return vec4(color, 1.0);
}
