vec4 Effect2(vec2 uv) {
    // Animate UV coordinates with time
    vec2 st = uv * 5.0 + vec2(sin(iTime)*3.0, cos(iTime*0.7)*2.0);

    // Create a flowing plasma effect using noise and turbulence
    float t1 = turbulent(st + vec2(iTime*0.5, iTime*0.3));
    float t2 = turbulent(st * 2.0 + vec2(iTime*0.7, iTime*0.4));

    // Combine multiple layers of noise for more complex patterns
    float value = (t1 + t2) * 0.5;

    // Create color transitions using HSL colors
    vec3 hslColor = vec3(
        sin(iTime*0.2 + value*2.0)*0.5 + 0.5,   // Hue
        0.7 + abs(sin(iTime*0.3 + value))*0.3,   // Saturation
        0.5 + t1 * 0.4                         // Lightness
    );

    // Convert HSL to RGB and mix with UV gradients
    vec3 color = hsl2rgb(hslColor);
    color *= sin(uv.x * 8.0 + iTime) * 0.5 + 0.5;
    color += pow(abs(sin(iTime*0.4 + uv.y*10.0)), 2.0) * 0.3;

    return vec4(color, 1.0);
}
