vec4 Effect2(vec2 uv) {
    // Create a time-varying base frequency
    float time = iTime * 0.5;

    // Convert UV coordinates to polar form
    vec2 st = uv - 0.5;
    float r = length(st);
    float a = atan2(st.y, st.x) + time;

    // Create a complex pattern using turbulence
    float t1 = turbulent(vec3(r * 8.0 + time, a * 4.0, 0.0));
    float t2 = turbulent(vec2(r * 16.0 - time * 2.0));

    // Combine turbulence results with noise
    float pattern = (t1 + t2) * 0.5;

    // Create a vibrant color palette using HSL
    vec3 color = hsl2rgb(vec3(
        fract(a * 0.2 + time * 0.1),
        0.7 + noise(vec2(time)) * 0.3,
        0.5 + pattern * 0.4
    ));

    // Add depth and movement to the color
    vec3 finalColor = mix(
        color,
        hsl2rgb(vec3(
            fract(a * 0.1 + time * 0.3),
            0.6,
            0.7
        )),
        smoothstep(0.0, 1.0, r)
    );

    // Apply some transparency based on distance from center
    return vec4(finalColor, 1.0 - smoothstep(0.0, 1.0, r * 2.0));
}
