vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 polar = vec2(length(uv - 0.5), atan2(uv.y - 0.5, uv.x - 0.5));

    // Create rotating pattern by adding time-based rotation
    float angle = polar.y + iTime * 0.5;

    // Use noise to add some variation
    float n = noise(vec3(polar.x * 10.0, angle * 2.0, iTime * 0.5));

    // Create color wheel effect using HSL colors
    vec3 color = hsl(
        mod(angle + n * 0.5, 2.0 * PI) / (2.0 * PI),  // Hue
        smoothstep(0.0, 1.0, polar.x * 2.0) * 0.7 + n * 0.3,  // Saturation
        0.5 + turbulent(vec3(polar.x, angle, iTime)) * 0.3  // Lightness
    );

    return vec4(hsl2rgb(color), 1.0);
}
