vec4 Effect2(vec2 uv) {
    // Time parameter scaled appropriately
    float t = iTime * 0.5;

    // Base noise for coral structure
    vec3 coralNoise = vec3(
        noise(uv * 10.0 + sin(t) * 2.0),
        noise(uv * 10.0 + cos(t) * 2.0),
        noise(uv * 10.0 + t)
    );

    // Create pulsating glow effect
    float glow = sin(iTime * 3.0 + uv.x * 5.0 + uv.y * 5.0);
    glow = (glow + 1.0) * 0.5; // Normalize to 0-1

    // Bioluminescent creatures movement pattern
    vec2 creaturePos = uv + vec2(sin(iTime + uv.x * 3.0), cos(iTime + uv.y * 3.0)) * 0.5;
    float creatureDensity = turbulent(creaturePos * 5.0);

    // Combine coral and creatures patterns
    float density = max(coralNoise.x, creatureDensity) * glow;

    // Color transformation with HSL to RGB
    vec3 color = hsl2rgb(vec3(
        (coralNoise.z + 0.5) * 180.0, // Hue variation
        0.7 + coralNoise.y * 0.3,     // Saturation
        density                       // Lightness
    ));

    // Add depth effect based on UV coordinates
    color *= (1.0 - length(uv * 2.0 - 1.0) * 0.5);

    return vec4(color, 1.0);
}
