vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create hexagonal grid pattern
    float ring = floor(r * 5.0); // Number of rings
    float angle = theta + iTime; // Add rotation over time

    // Calculate hexagon position
    vec2 hexPos = vec2(
        r * cos(angle),
        r * sin(angle)
    );
    // Add noise and turbulence for dynamic effect
    float noiseVal = turbulent(hexPos * 0.5 + iTime);

    // Create pulsating color pattern
    vec3 hslColor = vec3(
        mod(ring * 0.2 + iTime, 1.0), // Pulsating hue
        0.7 + noiseVal * 0.3,          // Saturation with noise
        0.5 + sin(iTime) * 0.5         // Pulsating lightness
    );
    // Convert HSL to RGB and output
    vec3 color = hsl2rgb(hslColor);

    return vec4(color * (1.0 + noiseVal * 0.5), 1.0);
}
