vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Base noise pattern with turbulence
    float baseNoise = turbulent(uv * 5.0 + iTime) * 0.5 + 0.5;

    // Calculate distance from center and angle
    float dist = length(uv);
    float angle = atan2(uv.y, uv.x);

    // Create swirling pattern with time
    float swirl = sin(iTime * 0.5 + angle * 4.0) * dist * 0.5;

    // Combine noise and swirling effect
    float finalNoise = baseNoise + swirl;

    // Color based on position and noise
    vec3 hslColor = vec3(
        fract(angle * 0.1 + iTime * 0.1),
        0.7,
        mix(0.2, 0.9, finalNoise)
    );

    // Convert to RGB and add glow effect
    color = hsl2rgb(hslColor) * (1.0 - dist * 0.5) * exp(-dist * 3.0);

    return vec4(color, 1.0);
}
