vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 10.0 + iTime / 2.0;

    // Create turbulence pattern
    float t = turbulent(st);
    t += turbulent(st * 3.0 + 1.0) * 0.5;

    // Generate color from HSL values
    vec3 color = hsl(
        sin(t * 4.0 + iTime) * 0.5 + 0.5,
        abs(sin(iTime / 2.0)) * 0.7 + 0.3,
        abs(cos(t * 2.0 + iTime)) * 0.6 + 0.4
    );

    // Add some color variation
    color = mix(color, hsl(
        sin(t * 5.0 + iTime) * 0.5 + 0.5,
        abs(sin(iTime / 3.0)) * 0.7 + 0.3,
        abs(cos(t * 4.0 + iTime)) * 0.6 + 0.4
    ), 0.3);

    return vec4(color, 1.0);
}
