vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Generate particles with noise and motion
    for (int i = 0; i < 3; i++) {
        float scale = pow(2.0, float(i)) * 5.0;
        float timeScale = iTime * (1.0 + float(i) * 0.5);

        // Particle position with noise offset
        vec2 pos = uv * scale + noise(vec3(uv * scale, iTime)) * 4.0;

        // Add motion to particles
        pos += sin(timeScale + pos.x * pi) * 0.5;

        // Calculate distance from center
        float dist = length(pos);

        // Create glowing effect with turbulence
        float glow = turbulent(vec2(dist, iTime)) * 0.5 + 0.5;

        // Dynamic color shifts using HSL
        vec3 hslColor = vec3(
            timeScale * 0.1,
            0.7 + sin(iTime) * 0.3,
            glow * 0.8 + 0.2
        );
        vec3 rgbColor = hsl2rgb(hslColor);

        // Add to final color with trail effect
        color += rgbColor * (1.0 - dist * 0.1) * pow(glow, 2.0);
    }

    return vec4(color, 1.0);
}
