vec4 Effect2(vec2 uv) {
    // Create a time-varying base color shift
    vec3 colorParams = vec3(
        0.5 * sin(iTime * 0.5 + uv.x * 10.0),
        0.5 * cos(iTime * 0.6 + uv.y * 10.0),
        0.0
    );

    // Add noise and turbulence effects
    float noise1 = turbulent(uv * 5.0);
    float noise2 = turbulent(vec3(uv * 2.0, iTime));

    // Create flowing liquid effect with color shifts
    vec2 flowUV = uv + vec2(0.1 * sin(iTime), 0.1 * cos(iTime)) * noise1;
    vec3 metalColor = hsl(
        0.5 + 0.5 * noise2,
        0.7 + 0.3 * noise1,
        0.3 + 0.2 * turbulent(flowUV * 4.0)
    );

    // Add dynamic color shifts based on mouse movement (simulated with time and noise)
    vec3 finalColor = metalColor * mix(
        hsl2rgb(vec3(0.5, 1.0, 0.5)),
        hsl2rgb(vec3(0.2, 1.0, 0.8)),
        0.5 + 0.5 * turbulent(uv * 10.0 + iTime)
    );

    return vec4(finalColor, 1.0);
}
