vec4 Effect2(vec2 uv) {
    // Create rotating pattern
    vec2 st = uv - 0.5;
    float angle = iTime * 0.1;
    st = mat2(cos(angle), sin(angle), -sin(angle), cos(angle)) * st;

    // Add pulsating effect
    float dist = length(st) * 4.0;
    float pulse = sin(iTime * 0.5 + dist) * 0.5 + 0.5;

    // Generate noise patterns
    vec3 noise1 = vec3(noise(vec2(dist, iTime)), noise(vec2(st.x*2.0, st.y*2.0)), noise(vec3(st*4.0, iTime)));
    float turbulence = turbulent(vec2(st * 2.0)) * 0.5 + 0.5;

    // Create color gradients
    vec3 color1 = hsl2rgb(vec3(0.6 + pulse * 0.3, 0.7, 0.8));
    vec3 color2 = hsl2rgb(vec3(0.9 - pulse * 0.2, 0.5, 0.7));

    // Combine patterns and colors
    vec3 finalColor = mix(color1, color2, turbulence);
    finalColor *= pulse;
    finalColor += noise1 * 0.3;

    return vec4(finalColor, 1.0);
}
