vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create a grid of points that move over time
    vec2 st = uv * 10.0; // Scale up for more cells
    vec2 ipos = floor(st);
    vec2 fpos = fract(st);

    // Add some movement to the grid points using sine waves
    vec2 pos = ipos + vec2(sin(time + ipos.x) * 0.5, cos(time + ipos.y) * 0.5);

    // Calculate distance from current point to moving grid points
    float d = length(fpos - fract(pos));

    // Add some turbulence to create an evolving pattern
    float turb = turbulent(uv * 10.0 + time);

    // Combine cellular noise with sine waves
    float wave = sin(st.x * 4.0 * PI) + cos(st.y * 4.0 * PI);
    float final = d * 0.5 + wave * 0.3 + turb * 0.2;

    // Convert to color using HSL
    vec3 color = hsl(final * 0.5 + time, 0.6, 0.5);

    return vec4(color, 1.0);
}
