vec4 Effect2(vec2 uv) {
    // Create base color using HSL with time-based hue variation
    vec3 baseColor = hsl(
        sin(iTime * 0.5 + uv.x * 10.0) * 0.5 + 0.5,  // Hue (with time animation)
        0.7,  // Saturation
        0.6   // Lightness
    );

    // Add layered sine wave patterns with varying frequencies and amplitudes
    float pattern = 
        sin(uv.x * 4.0 + iTime) * 0.5 +
        sin(uv.y * 3.0 + iTime * 2.0) * 0.3 +
        sin((uv.x + uv.y) * 6.0 + iTime * 1.5) * 0.2;

    // Add noise and turbulence for more dynamic effect
    float noiseValue = turbulent(uv * 8.0 + vec2(iTime)) * 0.3;

    // Combine patterns and noise to create final color variation
    vec3 finalColor = hsl2rgb(vec3(
        (baseColor.r + pattern * 0.4 + noiseValue) * 0.8,
        baseColor.g,
        baseColor.b
    ));

    return vec4(finalColor, 1.0);
}
