vec4 Effect2(vec2 uv) {
    // Convert to polar coordinates
    float theta = atan2(uv.y, uv.x);
    float r = length(uv);
    // Add time-based wobble to angle and radius
    theta += sin(r * 5.0 + iTime) * 0.5;
    theta += turbulent(vec3(theta, r, iTime * 0.1)) * 0.2;

    r += sin(iTime * 2.0 + theta * 4.0) * 0.3;
    r = mod(r, 2.0);
    // Create color based on polar coordinates
    vec3 color = hsl2rgb(vec3(
        (theta / (2.0 * PI) + 0.5) * 0.7 + 0.3,
        1.0 - abs(r - 1.0),
        0.5
    ));
    return vec4(color, 1.0);
}
