vec4 Effect2(vec2 uv) {
    // Create mirrored patterns by transforming UV coordinates
    vec2 st = fract(uv * 8.0);
    st = mix(st, 1.0 - st, step(0.5, st));

    // Convert to polar coordinates for geometric shapes
    vec2 polar = vec2(length(st - 0.5), atan2(st.y - 0.5, st.x - 0.5));

    // Add time-based color transitions
    float hue = iTime * 0.1 + sin(polar.y) * 0.5;
    vec3 color = hsl(hue, 1.0, 0.5);

    // Add noise and turbulence for dynamic patterns
    float noiseVal = turbulent(vec2(polar.x * 4.0, polar.y));
    color *= mix(1.0, noiseVal, 0.5);

    // Create layered effect with multiple frequencies
    vec3 finalColor = mix(color, hsv2rgb(vec3(hue + 0.5, 1.0, 0.5)), 0.3);

    return vec4(finalColor, 1.0);
}
