vec4 Effect2(vec2 uv) {
    vec2 st = uv - 0.5;
    st *= 4.0;

    float time = iTime * 0.1;
    st.x += sin(time) * 0.5;
    st.y += cos(time) * 0.5;

    vec2 p = st;
    float freq = 0.1 + 0.1 * sin(iTime * 0.5);
    float n = turbulent(p * freq);

    float r = length(st);
    vec3 color = hsl2rgb(vec3(0.5 + n * 0.5, 1.0, 0.5 + r * 0.5));

    return vec4(color, 1.0);
}
