vec4 Effect2(vec2 uv) {
    // Calculate direction from center
    vec2 dir = normalize(uv - 0.5);

    // Convert to polar coordinates
    float angle = atan2(dir.y, dir.x);
    float radius = length(uv - 0.5);

    // Create dynamic rays using sine waves and time
    float segments = 16.0;
    float rayIntensity = sin(angle + iTime * 0.5) / segments;
    rayIntensity = abs(fract(rayIntensity + 0.5));

    // Add noise for organic feel
    float noiseValue = noise(uv * 100.0);
    float intensity = mix(noiseValue, rayIntensity, 0.7);

    // Create color using HSL
    vec3 color = hsl2rgb(vec3(
        fract(iTime * 0.5 + angle / (2.0 * PI)),  // Hue over time and angle
        0.8,                                      // Saturation
        mix(0.5, 1.0, intensity)                  // Lightness based on intensity
    ));

    return vec4(color, 0.9);
}
