vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Create dynamic sine wave patterns in both X and Y directions
    float xWave = sin(uv.x * 8.0 + iTime) * 0.5 + 0.5;
    float yWave = sin(uv.y * 8.0 + iTime * 0.5) * 0.5 + 0.5;

    // Combine waves with noise and turbulence
    float pattern1 = sin(xWave * PI + yWave * PI) * 0.5 + 0.5;
    float pattern2 = noise(uv * 4.0 + iTime) * 0.5 + 0.5;

    // Create color transitions using HSL
    vec3 hslColor1 = vec3(
        (pattern1 + pattern2) * 360.0,
        0.7,
        0.8
    );

    vec3 hslColor2 = vec3(
        (turbulent(uv * 16.0 + iTime) + 0.5) * 360.0,
        0.6,
        0.7
    );

    // Mix colors based on patterns
    color += hsl2rgb(hslColor1) * pattern1;
    color += hsl2rgb(hslColor2) * (1.0 - pattern1);

    // Add time-based variations
    float timeFactor = sin(iTime * 0.5) * 0.5 + 0.5;
    color *= timeFactor + 0.5;

    return vec4(color, 1.0);
}
