vec4 Effect2(vec2 uv) {
    // Create base noise values
    float n = noise(uv * 5.0 + iTime);
    vec2 distortedUV = uv + sin(uv * 10.0 + iTime) * 0.1;

    // Add flowing motion
    vec2 flow = vec2(
        sin(uv.x * 3.0 + iTime * 2.0),
        cos(uv.y * 3.0 + iTime * 2.0)
    ) * 0.5;

    // Create ripples effect
    float ripples = turbulent(distortedUV * 4.0 + iTime) *
                   turbulent(distortedUV * 2.0 + iTime);

    // Generate color based on noise and time
    vec3 color = hsl2rgb(vec3(
        sin(iTime * 0.5 + n * pi) * 0.5 + 0.5,
        0.7 + ripples * 0.3,
        0.1 + abs(sin(iTime)) * 0.4
    ));

    // Add brightness variation
    color *= 0.8 + sin(n * 2.0 + iTime) * 0.2;

    return vec4(color, 1.0);
}
