vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);
    float time = iTime * 0.5;

    // Create rotating shape layers
    for(float i = 0.0; i < 3.0; i++) {
        float angle = time + i * PI/2.0;
        mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));

        vec2 st = uv * rot;
        st *= 5.0 + turbulent(vec3(st, time)) * 2.0;

        float d = length(st);
        float c = sin(d * 10.0) * 0.5 + 0.5;

        vec3 col = hsl2rgb(vec3(
            (time + i * 0.4) * 0.2,
            0.7,
            0.5 + turbulent(st.xy) * 0.2
        ));

        color += mix(color, col, c);
    }

    // Add noise texture overlay
    vec2 noiseScale = uv * 10.0;
    float n = noise(noiseScale) * 0.3;
    color += n;

    return vec4(color, 1.0);
}
