vec4 Effect2(vec2 uv) {
    // Rotate coordinates based on time
    float angle = sin(iTime * 0.5) * 0.5 + 0.5;
    vec2 rotatedUv = vec2(
        uv.x * cos(angle) - uv.y * sin(angle),
        uv.x * sin(angle) + uv.y * cos(angle)
    );

    // Create sine wave patterns with time-based rotation
    float waveX = sin(rotatedUv.x * 10.0 + iTime) * 0.5 + 0.5;
    float waveY = sin(rotatedUv.y * 10.0 - iTime) * 0.5 + 0.5;

    // Mix waves with turbulence
    float mixAmount = turbulent(vec2(waveX, waveY) * 4.0);
    vec3 colorBase = hsl(
        (iTime * 0.1 + rotatedUv.x * 0.5 + rotatedUv.y * 0.5) * 360.0,
        0.7,
        mixAmount * 0.6 + 0.4
    );

    // Add dynamic color transitions
    vec3 finalColor = hsv2rgb(vec3(
        (iTime * 0.1 + waveX * 0.5 + waveY * 0.5) * 360.0,
        0.7,
        mixAmount * 0.6 + 0.4
    ));

    // Add noise and brightness variation
    float brightness = turbulent(rotatedUv * 8.0) * 0.2 + 0.8;
    finalColor *= brightness;

    return vec4(finalColor, 1.0);
}
