vec4 Effect2(vec2 uv) {
    // Create base color with time-based hue
    vec3 color = hsl(0.5 + turbulent(uv * 10.0) * 0.5, 1.0, 0.5);

    // Add noise layer with time animation
    float n = noise(vec2(uv.x + iTime, uv.y)) * 50.0;
    color += hsl(0.3 + n * 0.1, 0.8, 0.6);

    // Create pulsing effect
    float pulse = sin(iTime * 0.5) * 0.5 + 0.5;
    color *= hsv2rgb(vec3(0.0, 0.0, pulse));

    return vec4(clamp(color, 0.0, 1.0), 1.0);
}
