vec4 Effect2(vec2 uv) {
    // Create polar coordinates from UV
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan(st.y, st.x) + iTime * 0.5; // Rotate over time
    float r = length(st);

    // Add wobbly effect using sine waves
    theta += sin(theta * 3.0 + iTime) * 0.2;
    r += sin(theta * 4.0 + iTime * 2.0) * 0.1;

    // Create twirly pattern using polar coordinates
    vec2 twirl = vec2(
        cos(theta) * r,
        sin(theta) * r
    );

    // Add sparkles using noise
    float sparkle = turbulent(vec3(twirl * 5.0, iTime)) *
                   turbulent(vec3(twirl * 10.0, iTime));

    // Create color transitions
    vec3 color = hsl2rgb(vec3(
        theta * 0.5 + iTime * 0.2,
        0.7 + sparkle * 0.3,
        0.5 + sin(iTime) * 0.2
    ));

    // Add sparkles overlay
    color = mix(color, vec3(1.0), sparkle * 0.5);

    return vec4(color, 1.0);
}
