vec4 Effect2(vec2 uv) {
    // Transform UV coordinates to create hexagonal grid pattern
    mat2 m = mat2(0.5, sqrt(3.0)/2.0, -sqrt(3.0)/2.0, 0.5);
    vec2 st = fract(uv * 10.0) * 2.0 - 1.0;
    st = m * st;

    // Calculate distance from center
    float d = length(st);
    vec2 coord = abs(st);

    // Create hexagonal pattern using transformed coordinates
    float hex = max(coord.x, (coord.y * sqrt(3.0) + 1.0)/2.0);

    // Add pulsing effect using sine waves
    float timePulse = sin(iTime * 0.5) * 0.5 + 0.5;
    vec3 color = hsl(hex * 360.0, 0.7, 0.5 * (sin(d * 4.0 - iTime * 2.0) * 0.5 + 0.5));

    // Apply turbulence and noise for additional effects
    vec3 noiseVec = vec3(noise(vec3(uv * 10.0, iTime)), noise(vec3(uv * 10.0 + 1.0, iTime)), noise(vec3(uv * 10.0 + 2.0, iTime)));
    float turb = turbulent(vec3(uv * 5.0, iTime));

    // Combine effects
    color *= mix(1.0, 1.0 - turb * 0.3, timePulse);
    color += noiseVec * 0.2;

    return vec4(color, 1.0);
}
