vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for spiral effect
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);

    // Add time-based rotation and pulsing
    theta += iTime * 0.5;
    r += sin(theta + iTime) * 0.3;

    // Create spiraling rings using modular arithmetic
    float ring = mod(r * 10.0 + iTime, 2.0);
    float intensity = abs(1.0 - ring);

    // Add turbulence for dynamic texture
    vec2 noiseUV = uv * 5.0 + iTime;
    float noiseVal = turbulent(noiseUV) * 0.3;
    intensity += noiseVal;

    // Create color shifts using HSL to RGB conversion
    vec3 hslColor = vec3(theta / (2.0 * PI), 1.0, 0.5 + intensity * 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
