vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for hexagonal pattern
    vec2 st = uv * 5.0; // Scale up for more cells
    float theta = atan2(st.y, st.x);
    float radius = length(st);

    // Create hexagonal grid pattern using sine and cosine
    float pattern = sin(theta * 6.0) + cos(theta * 6.0);
    pattern += turbulent(vec3(st, iTime)) * 0.5; // Add turbulence

    // Pulsating effect with time
    float pulse = sin(iTime * 2.0 + radius * 10.0) * 0.5 + 0.5;

    // Mouse interaction (assuming mouse position is available)
    vec2 mousePos = vec2(0.5, 0.5); // Replace with actual mouse input
    float mouseDist = length(mousePos - uv);
    float glow = max(1.0 - mouseDist * 4.0, 0.0) * pulse;

    // Color calculation using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        (theta + iTime) * 180.0 / PI,
        1.0,
        0.5 + glow * 0.5
    ));

    return vec4(color, 1.0);
}
