vec4 Effect2(vec2 uv) {
    // Create a base frequency using sine waves with varying frequencies
    float wave1 = sin(uv.x * 8.0 + iTime * 2.0) * 0.5;
    float wave2 = cos(uv.y * 6.0 - iTime * 3.0) * 0.4;
    float wave3 = sin((uv.x + uv.y) * 12.0 + iTime) * 0.3;

    // Combine waves with different frequencies and phases
    float combined = wave1 + wave2 + wave3;

    // Add turbulence to create more dynamic patterns
    vec2 noiseUV = uv * 4.0; // Scale UV for higher frequency noise
    float turb = turbulent(noiseUV) * 0.5;
    combined += turb;

    // Create color from HSL values with time-based hue variation
    vec3 color = hsl(
        fract(iTime * 0.2 + uv.x * 10.0), // Hue varies over time and space
        0.7, // Saturation
        0.5 + combined * 0.4 // Lightness modulated by wave pattern
    );

    return vec4(hsl2rgb(color), 1.0);
}
