vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    float r = length(uv);
    float theta = atan2(uv.y, uv.x);

    // Add time-based rotation
    theta += iTime * 0.5;

    // Create multiple frequency components for more interesting patterns
    vec3 layers = vec3(
        sin(r * 10.0 + theta * 3.0) * 0.5 + 0.5,
        sin(r * 20.0 + theta * 5.0) * 0.5 + 0.5,
        sin(r * 40.0 + theta * 7.0) * 0.5 + 0.5
    );

    // Add noise and turbulence
    float noise1 = turbulent(vec2(theta, r)) * 0.5 + 0.5;
    float noise2 = turbulent(vec2(theta * 2.0, r * 2.0)) * 0.5 + 0.5;

    // Create color shifts using HSL to RGB conversion
    vec3 color = hsl(
        theta * 0.1 + iTime * 0.1,
        noise1 * 0.7 + 0.3,
        noise2 * 0.6 + 0.4
    );

    // Combine layers and colors
    color *= mix(layers.x, layers.y, noise1);
    color = mix(color, vec3(1.0), layers.z * 0.5);

    // Add some contrast
    color = (color - 0.5) * 2.0;

    return vec4(color, 1.0);
}
