vec4 Effect2(vec2 uv) {
    // Create wobbly motion using sine waves and time
    vec2 offset = sin(iTime * 0.5 + uv * 10.0) * 0.3;
    vec2 distortedUV = uv + offset;

    // Add twirling effect by rotating coordinates around center
    vec2 center = vec2(0.5);
    vec2 polar = distortedUV - center;
    float angle = atan(polar.y, polar.x) + iTime * 2.0;
    float dist = length(polar) * 0.8;

    // Create sparkles using noise and turbulence
    float sparkle = turbulent(vec3(uv * 10.0, iTime)) * 0.5 + 0.5;
    float blend = sin(dist * 10.0 + iTime) * 0.5 + 0.5;

    // Combine colors with HSL to RGB conversion
    vec3 color1 = hsl2rgb(vec3(angle * 0.1, 0.7, 0.8));
    vec3 color2 = hsl2rgb(vec3(angle * 0.1 + 0.5, 0.6, 0.7));

    // Mix colors and add sparkle effect
    vec3 finalColor = mix(color1, color2, blend) * (1.0 + sparkle * 0.5);

    return vec4(finalColor, 1.0);
}
