vec4 Effect2(vec2 uv) {
    float scale = 8.0;
    vec2 st = uv * scale;

    // Create grid pattern with noise variations
    vec2 cell = floor(st);
    vec2 pos = fract(st + sin(iTime * 0.5 + cell.x * 10.0) * 0.3);

    // Add movement and complexity
    float timeFactor = iTime * 0.1;
    pos += vec2(sin(timeFactor + cell.y * 10.0), cos(timeFactor + cell.x * 10.0)) * 0.2;

    // Use noise to create organic patterns
    float n = noise(vec3(pos, iTime * 0.5));
    n = mod(n * 4.0 + timeFactor, 1.0);

    // Create color transitions with shifting hues
    vec3 hslColor = vec3((n * 0.6 + 0.2) + timeFactor * 0.2, 0.8, 0.7);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add smooth transitions between colors
    float gradient = smoothstep(0.1, 0.9, n);
    rgbColor = mix(vec3(0.1), rgbColor, gradient);

    return vec4(rgbColor, 1.0);
}
