vec4 Effect2(vec2 uv) {
    // Create two sine waves with different frequencies and offsets
    float wave1 = sin(uv.x * 5.0 + iTime) * 0.5;
    float wave2 = sin(uv.y * 5.0 + iTime * 0.7) * 0.5;

    // Combine the waves with varying amplitudes
    vec2 combined = vec2(wave1, wave2);
    combined *= turbulent(vec3(uv * 4.0, iTime * 0.1)) * 0.5 + 0.5;

    // Create color variations using HSL
    vec3 color = hsl(
        (wave1 + wave2) * 0.5 + 0.5,
        0.7,
        0.6
    );

    // Convert to RGB and output
    return vec4(hsl2rgb(color), 1.0);
}
