vec4 Effect2(vec2 uv) {
    // Create radial gradient from center
    vec2 pos = uv - 0.5;
    float dist = length(pos);
    dist *= 10.0; // Scale the distance

    // Add oscillating sine wave effect
    float timeFactor = sin(iTime * 4.0) * 0.5 + 0.5;
    float value = dist + sin(dist * 2.0 + iTime * 2.0) * 0.5;

    // Create color transitions using HSL
    vec3 color = hsl(value * 0.6 + timeFactor, 1.0, 0.5);

    // Add noise for texture
    float noiseValue = noise(uv * 8.0 + iTime) * 0.2;
    value += noiseValue;

    // Final color with alpha
    return vec4(hsl2rgb(vec3(value * 0.6 + timeFactor, 1.0, 0.5)), 1.0);
}
