vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 pos = uv * 2.0 - 1.0;
    float r = length(pos);
    float theta = atan2(pos.y, pos.x);
    // Add time-based rotation
    theta += iTime * 0.5;
    // Create a swirling pattern using sine distortion
    r *= sin(theta * 3.0 + iTime) * 0.5 + 1.0;

    // Convert back to Cartesian coordinates
    vec2 newUv = vec2(
        cos(theta) * r,
        sin(theta) * r
    );
    // Add turbulence for dynamic effect
    float turb = turbulent(newUv * 4.0 + iTime);
    turb += turbulent(vec3(newUv * 16.0, iTime)) * 0.5;

    // Create color based on polar coordinates and turbulence
    vec3 color = hsl2rgb(vec3(
        theta * 0.15 + 0.5,
        0.7 + turb * 0.3,
        0.5 + (1.0 - r) * 0.5
    ));
    // Add noise for texture
    color += noise(pos * 8.0 + iTime) * 0.2;

    return vec4(color, 1.0);
}
