vec4 Effect2(vec2 uv) {
    // Create wave patterns using sine and cosine functions
    float speed = 0.5;
    float time = iTime * speed;

    // Wave parameters
    vec2 freq1 = vec2(4.0, 3.0);
    vec2 freq2 = vec2(2.0, 4.0);

    // Generate wave patterns with different frequencies and phases
    float wave1 = sin(uv.x * freq1.x + time) + cos(uv.y * freq1.y + time + PI/2.0);
    float wave2 = sin(uv.y * freq2.x - time) + cos(uv.x * freq2.y - time + PI);

    // Combine waves and add turbulence
    vec2 combined = vec2(wave1, wave2);
    float turb = turbulent(combined * 5.0 + time);

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        mod(time * 0.1, 1.0),   // Hue variation over time
        0.7,                    // Saturation
        0.5 + turb * 0.5        // Lightness influenced by turbulence
    );

    // Convert HSL to RGB and mix with wave patterns
    vec3 rgbColor = hsl2rgb(hslColor);
    vec3 finalColor = rgbColor * abs(wave1) * abs(wave2);

    return vec4(finalColor, 1.0);
}
