vec4 Effect2(vec2 uv) {
    // Create a base pattern using sine waves with different frequencies
    float pattern = sin(uv.x * 4.0 + iTime) * cos(uv.y * 3.0 + iTime * 2.0);

    // Add noise and turbulence for more dynamic patterns
    vec2 noiseUV = uv * 10.0 + vec2(iTime, iTime * 0.5);
    float noiseVal = noise(noiseUV) * 0.5;
    pattern += turbulent(vec3(uv * 4.0, iTime)) * 0.3;

    // Create color shifts using HSL to RGB conversion
    vec3 color1 = hsl2rgb(vec3(0.5 + sin(iTime * 0.5) * 0.5, 1.0, 0.8));
    vec3 color2 = hsl2rgb(vec3(0.3 + cos(iTime * 0.6) * 0.5, 1.0, 0.7));

    // Mix colors based on pattern and noise
    vec3 finalColor = mix(color1, color2, abs(pattern * 0.5 + noiseVal * 0.5));

    return vec4(finalColor, 1.0);
}
