vec4 Effect2(vec2 uv) {
    // Center and scale UV coordinates
    vec2 st = (uv - 0.5) * 2.0;

    // Add rotation based on time
    float angle = iTime * 2.0 * PI;
    float ca = cos(angle);
    float sa = sin(angle);
    vec2 rotated = vec2(
        st.x * ca - st.y * sa,
        st.x * sa + st.y * ca
    );

    // Create tiling pattern
    vec2 tile = fract(abs(rotated) * 4.0);

    // Generate vibrant colors using HSL
    float hue = (iTime * 0.5 + length(tile)) * PI;
    vec3 color = hsl(hue, 1.0, 0.5);

    // Add noise and turbulence for dynamic effect
    float noiseVal = turbulent(vec2(iTime, tile.x));
    color *= mix(0.8, 1.2, noiseVal);

    return vec4(color, 1.0);
}
