vec4 Effect2(vec2 uv) {
    // Create flowing pattern using noise and time
    vec2 st = uv * 5.0 + iTime;

    // Add turbulence for cascading effect
    float t = turbulent(st);

    // Calculate depth based on position and time
    float d = length(uv - vec2(0.5, 0.5)) * 3.0 + iTime * 2.0;

    // Create shimmering color transitions using HSL
    vec3 c = hsl(d * 0.1, sin(iTime) * 0.5 + 0.5, cos(d * 0.5 + iTime) * 0.5 + 0.5);

    // Convert to RGB and apply turbulence for dynamic effect
    vec3 color = hsl2rgb(c) * (1.0 + sin(t * 4.0 + iTime * 2.0)) * 0.5;

    return vec4(color, 1.0);
}
