vec4 Effect2(vec2 uv) {
    // Create base coral structure using noise
    float noise1 = noise(uv * 5.0 + iTime);
    float noise2 = turbulent(uv * 3.0 + iTime * 0.5);

    // Generate pulsating color patterns
    vec3 color1 = hsl(noise1 * pi, 0.8, 0.6);
    vec3 color2 = hsl((noise2 + noise1) * pi, 0.7, 0.5);

    // Combine colors with time-based variation
    float mixAmount = sin(iTime * 0.5 + uv.x * 4.0) * 0.5 + 0.5;
    vec3 finalColor = mix(color1, color2, mixAmount);

    // Add pulsating effect using sine wave
    float pulse = sin(iTime * 2.0 + noise1 * pi) * 0.3 + 0.7;
    finalColor *= pulse;

    // Create depth variation
    vec2 st = uv * 4.0;
    float depth = length(st);
    depth = smoothstep(0.0, 4.0, depth);
    finalColor *= depth;

    // Add turbulence effect
    vec3 turbPos = vec3(uv * 2.0 + iTime, iTime * 0.5);
    float turb = turbulent(turbPos) * 0.5 + 0.5;
    finalColor = mix(finalColor, finalColor * turb, 0.4);

    // Final output with gamma correction
    return vec4(pow(finalColor, vec3(1.2)), 1.0);
}
