vec4 Effect2(vec2 uv) {
    // Create a grid of triangles with dynamic tessellation
    vec2 st = uv * 10.0 + iTime; // Scale and animate the grid

    // Calculate triangle rotation
    float angle = sin(iTime * 0.5 + st.x * 3.14159) * 0.5;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));

    // Create rotating triangles pattern
    vec2 rotatedUV = rot * (st - floor(st)) * 0.5 + 0.5;

    // Generate color gradients using HSL
    vec3 hslColor = vec3(
        mod(iTime * 0.1 + rotatedUV.x * 2.0, 1.0), // Hue variation over time and space
        0.7, // Saturation
        0.5 // Lightness
    );

    // Convert HSL to RGB
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add turbulence for dynamic effect
    float turb = turbulent(rotatedUV * 10.0 + iTime);
    rgbColor *= 1.0 + turb * 0.5;

    // Create gradient from center of triangles
    vec2 centerDist = rotatedUV - 0.5;
    float dist = length(centerDist) * 3.0;
    float gradient = smoothstep(0.0, 1.0, sin(dist * pi + iTime));

    // Combine everything for final color
    return vec4(rgbColor * gradient, 1.0);
}
