vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;
    float speed = 0.7;
    float scale = 3.0;
    float decay = 0.9;
    // Particle position with oscillation
    vec2 pos = uv * 2.0 - 1.0;
    pos.x += sin(time + pos.y * scale) * 0.5;
    pos.y += cos(time * speed + pos.x * scale) * 0.5;

    // Color based on position and time
    vec3 color = hsl2rgb(vec3(
        sin(pos.x * 10.0 + time) * 0.5 + 0.5,
        abs(sin(time * 0.7 + pos.y * 5.0)) * 0.5 + 0.5,
        0.8
    ));

    // Add glow effect using turbulence
    float glow = turbulent(vec3(pos, time)) * 0.2;
    color += vec3(glow) * 1.5;

    // Control particle existence over time
    float exist = sin(time * PI + pos.x * 10.0);
    exist = abs(exist);
    exist = pow(exist, decay);

    return vec4(color * exist, 1.0);
}
