vec4 Effect2(vec2 uv) {
    // Create pulsating sine wave pattern with time-based RGB shifts

    // Base wave pattern
    vec2 st = sin(uv * 10.0 + iTime);
    st += sin(uv * 5.0 - iTime); // Add another wave for complexity

    // Pulsating effect using time modulation
    float pulse = sin(iTime) * 0.5 + 0.5;

    // RGB color shifts with different frequencies
    vec3 color = vec3(
        sin(st.x * 2.0 + iTime * 0.5), // Red channel
        sin(st.y * 2.0 + iTime * 0.7 + 1.0), // Green channel
        sin((st.x + st.y) * 2.0 + iTime * 1.0 + 2.0) // Blue channel
    );

    // Add noise and turbulence for more dynamic effect
    float noiseVal = noise(uv * 5.0 + vec2(iTime));
    color += turbulent(vec3(uv, iTime)) * 0.3;

    // Scale and mix colors
    color *= pulse * 1.5;
    color = mix(vec3(0.0), color, 0.6);

    return vec4(color, 1.0);
}
