vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5; // Add rotation

    // Create hexagonal grid pattern
    const float hexScale = 8.0;
    vec2 hexCoords = vec2(r * cos(theta), r * sin(theta)) * hexScale;

    // Use turbulence to create dynamic patterns
    float turb = turbulent(hexCoords + iTime);

    // Calculate color based on position and time
    vec3 colorHSL = vec3(
        fract((theta / (pi * 2.0)) + iTime * 0.1), // Hue variation
        0.8, // Saturation
        0.5 + 0.5 * sin(iTime) // Pulsating lightness
    );

    // Convert HSL to RGB and mix with turbulence
    vec3 finalColor = hsl2rgb(colorHSL) * (0.5 + 0.5 * turb);

    return vec4(finalColor, 1.0);
}
