vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 10.0 + iTime;

    // Create a complex noise pattern using multiple octaves
    float n = turbulent(st);

    // Generate color based on position and time
    vec3 color = hsl(
        0.5 + turbulent(vec2(uv * 20.0 + iTime)) * 0.5, 
        0.7,
        0.5 + sin(iTime) * 0.2
    );

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(color), 1.0);
}
