vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create expanding rings effect with time
    r *= iTime * 0.5;

    // Generate base color using HSL
    vec3 color = hsl(r + theta * 0.1, 0.7, 0.6);

    // Add noise texture for complexity
    float noiseVal = noise(vec2(uv.x * 4.0, uv.y * 4.0) + iTime);
    color += vec3(noiseVal * 0.3);
    // Create turbulence effect
    float turb = turbulent(vec2(r * 0.5, theta));
    color *= mix(1.0, 1.5, turb);
    // Add lighting effects
    float glow = smoothstep(0.8, 1.0, r);
    color += vec3(glow * 0.4);
    return vec4(color, 1.0);
}
