vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Create a grid of nodes using noise and time
    float nodePulse = turbulent(uv * 10.0 + iTime) * sin(iTime * 0.5) + 0.5;

    // Calculate distance to nearest node points
    vec2 distToNode = min(abs(uv - floor(uv)), abs(uv - ceil(uv)));
    float nodeDistance = length(distToNode);

    // Create glowing nodes
    if (nodeDistance < 0.05) {
        color += vec3(1.0) * (1.0 + nodePulse * 2.0);
    }

    // Calculate distance to edges between nodes
    float edgeDistance = min(length(uv - vec2(0.5)), length(uv - vec2(0.5, 0.0)));
    edgeDistance = min(edgeDistance, length(uv - vec2(0.0, 0.5)));

    // Create pulsing connections between nodes
    if (edgeDistance < 0.1) {
        float hue = fract(iTime * 0.3 + uv.x * 0.7);
        color += hsl(hue, 1.0, 0.5).rgb;
    }

    // Add some glow around the nodes
    if (nodeDistance < 0.1) {
        color += vec3(1.0) * (1.0 - nodeDistance / 0.1);
    }

    return vec4(color, 1.0);
}
