vec4 Effect2(vec2 uv) {
    // Grid coordinates
    vec2 st = uv * 20.0;
    vec2 i = floor(st + 0.5);

    // Time-based animation
    float time = iTime * 0.5;

    // Calculate distance from grid points
    vec2 pos = fract(st) - 0.5;
    float dist = length(pos);

    // Pulsating effect with noise modulation
    float pulse = sin(time + dist * 10.0 + noise(vec3(i, time))) * 0.5 + 0.5;

    // Color transitions using HSL
    vec3 color = hsl2rgb(vec3(
        fract((i.x + i.y) * 0.1 + time),
        0.7,
        0.5 + pulse * 0.3
    ));

    // Add depth variation
    color *= 0.8 + noise(vec3(uv * 2.0, time)) * 0.2;

    return vec4(color, 1.0);
}
