vec4 Effect2(vec2 uv) {
    // Transform UV coordinates for hexagonal grid
    mat2 m = mat2(1.0, -0.5, 0.0, sqrt(3.0)/2.0);
    vec2 st = m * (uv * 2.0 - 1.0);
    // Calculate distance to nearest grid points
    vec2 i_st = floor(st + 0.5);
    vec2 f_st = fract(st + 0.5);

    // Create pulsating radius with time and noise
    float pulse = sin(iTime * 0.5) * 0.4 + 0.6;
    float noiseVal = noise(vec3(i_st, iTime)) * 0.1;
    float radius = pulse + noiseVal;
    // Check distance to nearest points
    vec2 d = min(f_st, 1.0 - f_st);
    float dist = length(d) * sqrt(3.0);
    // Create color based on distance and time
    vec3 color;
    if (dist < radius) {
        float hue = fract(length(i_st) * 0.1 + iTime * 0.2);
        color = hsl2rgb(vec3(hue, 1.0, 0.5));
    } else {
        color = vec3(0.0);
    }
    return vec4(color, 1.0);
}
