vec4 Effect2(vec2 uv) {
    // Create a flowing pattern using noise and turbulence
    vec3 color = vec3(0.0);

    // Generate base noise patterns with time variation
    float n1 = noise(uv * 100.0 + iTime);
    float n2 = turbulent(uv * 50.0 + iTime) * 0.5;
    float flow = (n1 + n2) * 0.5;

    // Add movement to the pattern
    vec2 pos = uv - 0.5;
    float dist = length(pos);
    float angle = atan2(pos.y, pos.x);

    // Create color variations using HSL
    vec3 hslColor = vec3(
        sin(iTime * 0.1 + flow * 2.0) * 0.5 + 0.5,
        0.7 + abs(flow) * 0.3,
        0.5 + dist * 0.5
    );

    // Convert to RGB and mix with distance-based color
    vec3 rgbColor = hsl2rgb(hslColor);
    rgbColor = mix(rgbColor, vec3(1.0), turbulent(vec3(uv * 20.0, iTime)) * 0.5 + 0.5);

    // Add glowing effect using distance from center
    float glow = smoothstep(0.7, 1.0, dist) * 0.5;
    rgbColor += glow;

    return vec4(rgbColor, 1.0);
}
