vec4 Effect2(vec2 uv) {
    // Create a time-varying distortion pattern
    vec2 st = uv * 5.0 + iTime;

    // Add turbulence to create complex patterns
    float turb = turbulent(st);

    // Generate color using noise and HSL conversion
    vec3 color = hsl2rgb(vec3(
        noise(vec3(uv, iTime)) * 0.6 + 0.4,
        0.7,
        0.5
    ));

    return vec4(color, 1.0);
}
